import React, { Component } from 'react';


class Dice extends Component{
    constructor(props){
        super(props)
        this.state = {
            picLog: ["url('dice1.jpg')", "url('dice2.jpg')",
                "url('dice3.jpg')", "url('dice4.jpg')",
                "url('dice5.jpg')", "url('dice6.jpg')"]
        }
}
    render() {
        let { logandroll, diceNum} = this.props
        let { picInd, picLog } = this.state
        return(
            <div>
                <div>
                    <h3>{diceNum}</h3>
                    <button id= "diceButton" style = {{backgroundImage:picLog[picInd]}}  onClick = {this.props.logandroll}></button>
                </div>
            </div>
        );
    }
}

export default Dice
