import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import Dice from './dice.js'
import Log from './dicelog.js'

class App extends Component {
    constructor(props){
        super(props)
            this.state = {
                diceLog: [],
                diceNum: 1,
                picInd:0,
                extraDice: [],

            }
    }
    logandroll = () => {
        this.rolldice()
        this.logHandler()
    }
    logHandler = () => {
        let {diceLog, diceNum} = this.state
        let newRandom = diceLog.push(diceNum)
        this.setState({diceLog: diceLog})

    }
    rolldice = () => {
        let { picInd } = this.state
        let random = Math.floor(Math.random()*6+1)
        let picselect = random -1
        this.setState({diceNum: random})
        this.setState({picInd: picselect})

    }
    refreshState = () =>{
        let { diceLog } = this.state
        this.setState ({diceLog : []})

    }
    addDice = () => {
        let {extraDice} = this.state
        let newDice = extraDice.push(<Dice />)
        this.setState({extraDice: newDice})
        console.log(newDice);

    }
    render(){
        let { diceNum, diceLog, picLog, picInd} = this.state
      return (
        <div className="App">
            <h1>DiceRoller</h1>
            <div className = "row">
              <div className = "card col-sm-6">
                  <Dice
                  logandroll = {this.logandroll}
                  diceNum= {diceNum}
                  picInd = {picInd}
                  picLog = {picLog}/>

              </div>
              <div className = "card col-sm-6">
                  <Log
                  rollLog = {diceLog}/>
                  <button className = "btn btn-primary" id = "refreshbtn"onClick = {this.refreshState}>Refresh Log</button>
                </div>
                <button onClick= {this.addDice}></button>
            </div>
        </div>
      );
    }
}
export default App;
