import React, { Component } from 'react';


class Dice extends Component{
    constructor(props){
        super(props)
        this.state = {
            diceNum: 1
        }
    }

    rolldice = () => {
        let { diceNum } = this.state
        let random = Math.floor(Math.random()*6+1)
        this.setState({diceNum: random})
        console.log(diceNum);

    }


    render() {
        let { diceNum } = this.state
        return(
            <div>
                <div>
                    <h3>{ diceNum }</h3>
                    <button onClick = {this.rolldice}> press </button>
                </div>
            </div>
        );
    }
}

export default Dice
