import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import Dice from './dice.js'
import Log from './dicelog.js'

class App extends Component {
    constructor(props){
        super(props)
            this.state = {
                diceLog: [],
                diceNum: 1,
            }
    }
    logandroll = () => {
        this.rolldice()
        this.logHandler()
    }
    logHandler = () => {
        let {diceLog, diceNum} = this.state
        let newRandom = diceLog.push(diceNum)
        // console.log(newRandom);
        this.setState({diceLog: diceLog})
        console.log(diceLog);

    }
    rolldice = () => {
        // let { } = this.state
        let random = Math.floor(Math.random()*6+1)
        this.setState({diceNum: random})
    }
    render(){
        let { diceNum, diceLog} = this.state
      return (
        <div className="App">
          <h1>Hello World</h1>
          <Dice
          rollFunction = {this.rolldice}
          randomNum = {diceNum}/>
          <button onClick = {this.logandroll}>Roll Dice</button>
          <Log
          rollLog = {diceLog}/>
        </div>
      );
    }
}
export default App;
