import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import Dice from './dice.js'
import Log from './dicelog.js'

class App extends Component {
    constructor(props){
        super(props)
            this.state = {
                diceLogMaster: [],
                extraDice: [],

            }
    }
    logandroll = () => {
        this.rolldice()
        this.logHandler()
    }
    logHandler = () => {
        let {diceLogMaster, diceNum} = this.state
        let newRandom = diceLogMaster.push(diceNum)
        this.setState({diceLogMaster: diceLogMaster})

    }
    // rolldice = () => {
    //     let { picInd } = this.state
    //     let random = Math.floor(Math.random()*6+1)
    //     let picselect = random -1
    //     this.setState({diceNum: random})
    //     this.setState({picInd: picselect})
    //
    // }
    refreshState = () =>{
        let { diceLogMaster } = this.state
        this.setState ({diceLogMaster : []})

    }
    addDice = () => {
        let {extraDice, diceNum, picInd, picLog} = this.state
        extraDice.push(<Dice logandroll = {this.logandroll}/>)
        this.setState({extraDice: extraDice})
        console.log(diceNum);

    }
    render(){
        let { diceNum, diceLogMaster, picLog, picInd, extraDice} = this.state
      return (
        <div className="App">
            <h1>DiceRoller</h1>
            <div className = "row">
              <div className = "card col-sm-6">
                  <Dice
                  logandroll = {this.logandroll}
                  diceNum= {diceNum}
                  picInd = {picInd}
                  picLog = {picLog}/>
                  <div>{extraDice}</div>
              </div>
              <div className = "card col-sm-6">
                  <Log
                  rollLog = {diceLogMaster}/>
                  <button className = "btn btn-primary" id = "refreshbtn"onClick = {this.refreshState}>Refresh Log</button>
                </div>
                <div>

                </div>
                <button onClick= {this.addDice}>Press ME</button>
            </div>
        </div>
      );
    }
}
export default App;
