import React, { Component } from 'react';


class Dice extends Component{
    constructor(props){
        super(props)
        this.state = {
            picInd:0 ,
            diceNum: 1,
            picLog: ["url('dice1.jpg')", "url('dice2.jpg')",
                "url('dice3.jpg')", "url('dice4.jpg')",
                "url('dice5.jpg')", "url('dice6.jpg')"],
            diceLog: []
        }
}
        rolldice = () => {
            let { picInd } = this.state
            let random = Math.floor(Math.random()*6+1)
            let picselect = random -1
            this.setState({diceNum: random})
            this.setState({picInd: picselect})
        }
        render() {
        let { picInd, logandroll, diceNum} = this.props
        let {  picLog } = this.state
        return(
            <div>
                <div>
                    <h3>{diceNum}</h3>
                    <button id= "diceButton" style = {{backgroundImage:picLog[picInd]}}  onClick = {this.props.logandroll}></button>
                </div>
            </div>
        );
    }
}

export default Dice
