import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import Dice from './dice.js'
import Log from './dicelog.js'

class App extends Component {
    constructor(props){
        super(props)
            this.state = {
                diceLog: [],
                diceNum: 1,
                picInd:0,
                picLog: ["url('dice1.jpg')", "url('dice2.jpg')",
                    "url('dice3.jpg')", "url('dice4.jpg')",
                    "url('dice5.jpg')", "url('dice6.jpg')"]
            }
    }
    logandroll = () => {
        this.rolldice()
        this.logHandler()
    }
    logHandler = () => {
        let {diceLog, diceNum} = this.state
        let newRandom = diceLog.push(diceNum)
        this.setState({diceLog: diceLog})

    }
    rolldice = () => {
        let { picInd } = this.state
        let random = Math.floor(Math.random()*6+1)
        let picselect = random -1
        this.setState({diceNum: random})
        this.setState({picInd: picselect})
        console.log(picInd);

    }
    refreshState = () =>{
        let { diceLog } = this.state
        this.setState ({diceLog : []})

    }
    render(){
        let { diceNum, diceLog, picLog, picInd} = this.state
      return (
        <div className="App">
            <h1>DiceRoller</h1>
            <div className = "row">
              <div className = "card col-sm-6">
                  <Dice
                  rollFunction = {this.rolldice}
                  randomNum = {diceNum}/>
                  <button id= "diceButton" style = {{backgroundImage:picLog[picInd]}}  onClick = {this.logandroll}></button>
              </div>
              <div className = "card col-sm-6">
                  <Log
                  rollLog = {diceLog}/>
                  <button className = "btn btn-primary"onClick = {this.refreshState}>Refresh Log</button>
                </div>
            </div>
        </div>
      );
    }
}
export default App;
